// Extract input data
const body = $input.first().json.body || $input.first().json;
const sessionId = body.sessionId || 'session_' + Date.now();
const conversationId = body.conversationId || null;
const message = body.message || '';

// Generate title from first message (first 50 chars)
const title = message.substring(0, 50) + (message.length > 50 ? '...' : '');

return [{
  json: {
    sessionId,
    conversationId,
    message,
    title,
    isNewConversation: !conversationId,
    startTime: Date.now()
  }
}];

// Merge created conversation with input
const input = $('ğŸ“¥ Parse Input').first().json;
const created = $input.first().json;

return [{
  json: {
    ...input,
    conversationId: created.id,
    title: created.title
  }
}];

// Prepare data for debate workflow
const prevData = $('ğŸ”€ Merge Paths').first().json;
const userMsgId = $input.first().json.id;

return [{
  json: {
    ...prevData,
    userMessageId: userMsgId
  }
}];

// Parse debate response and extract data
const debateResponse = $input.first().json;
const prepData = $('ğŸ“‹ Prep for Debate').first().json;
const startTime = prepData.startTime;
const duration = Date.now() - startTime;

// Extract output - handle different response formats
let output = '';
if (typeof debateResponse === 'string') {
  output = debateResponse;
} else if (debateResponse.output) {
  output = debateResponse.output;
} else if (debateResponse.text) {
  output = debateResponse.text;
} else if (debateResponse.response) {
  output = debateResponse.response;
} else {
  output = JSON.stringify(debateResponse);
}

// Parse stats from output
let consensus = 0, rounds = 0, decisions = 0;
const statsMatch = output.match(/ğŸ“Š.*?(\d+)%.*?consensus.*?(\d+).*?round.*?(\d+).*?decision/i);
if (statsMatch) {
  consensus = parseInt(statsMatch[1]) || 0;
  rounds = parseInt(statsMatch[2]) || 0;
  decisions = parseInt(statsMatch[3]) || 0;
}

// Generate title from topic if first message
let title = prepData.title;
if (prepData.isNewConversation && prepData.message) {
  // Extract a better title from the message
  const words = prepData.message.split(' ').slice(0, 6).join(' ');
  title = words + (prepData.message.split(' ').length > 6 ? '...' : '');
}

// Create sources from the 5 AI models
const sources = [
  { model: 'GPT-4o', icon: 'ğŸ§ ', role: 'Primary Analyst', contribution: 'Generated initial proposal and structured analysis' },
  { model: 'Gemini 2.0', icon: 'ğŸ”', role: 'Challenger', contribution: 'Questioned assumptions and identified gaps' },
  { model: 'Grok 3', icon: 'ğŸ˜ˆ', role: 'Devils Advocate', contribution: 'Presented counter-arguments and risks' },
  { model: 'DeepSeek R1', icon: 'ğŸ”¬', role: 'Technical Expert', contribution: 'Validated technical feasibility' },
  { model: 'Llama 3.3', icon: 'ğŸ¨', role: 'Creative', contribution: 'Suggested innovative approaches' }
];

// Create activity log
const activity = [
  { type: 'thinking', content: 'Analyzed request and prepared debate parameters', duration_ms: 2000 },
  { type: 'proposal', content: 'GPT-4o generated initial proposal', duration_ms: 5000 },
  { type: 'debate', content: '5 AI models debated and refined the proposal', duration_ms: duration - 10000 },
  { type: 'consensus', content: `Reached ${consensus}% consensus after ${rounds} rounds`, duration_ms: 2000 },
  { type: 'synthesis', content: 'Synthesized final response with all perspectives', duration_ms: 1000 }
];

return [{
  json: {
    conversationId: prepData.conversationId,
    sessionId: prepData.sessionId,
    userMessageId: prepData.userMessageId,
    title: title,
    output: output,
    consensus: consensus,
    rounds: rounds,
    decisions: decisions,
    duration: duration,
    sources: sources,
    activity: activity,
    stats: { consensus, rounds, decisions }
  }
}];

// Get assistant message ID and merge with response data
const responseData = $('ğŸ“Š Parse Response').first().json;
const assistantMsgId = $input.first().json.id;

return [{
  json: {
    ...responseData,
    assistantMessageId: assistantMsgId
  }
}];

// Save sources to database
const data = $('ğŸ”— Merge Msg ID').first().json;
const sources = data.sources || [];

// Return sources with message ID for batch insert
return sources.map(source => ({
  json: {
    messageId: data.assistantMessageId,
    model_name: source.model,
    model_icon: source.icon,
    model_role: source.role,
    contribution: source.contribution
  }
}));

// Prepare final response for the UI
const data = $('ğŸ”— Merge Msg ID').first().json;

return [{
  json: {
    conversation_id: data.conversationId,
    session_id: data.sessionId,
    title: data.title,
    output: data.output,
    sources: data.sources,
    activity: data.activity,
    stats: data.stats
  }
}];
